import React, { useState } from "react";
import css from "./Task4.module.css";

const Disabled = false;

function Task4() {
  const [like, setLike] = useState(12);
  const [dislike, setDislike] = useState(2);

  const handleLike = (e) => {
    e.preventDefault();
    if (like === 12) {
      setLike(like + 1);
      setDislike(2);
    }
    if (dislike === 3) return Disabled;
  };

  const handleDislike = (e) => {
    e.preventDefault();
    if (dislike === 2) {
      setDislike(dislike + 1);
      setLike(12);
    }
  };

  return (
    <div className={css.container}>
      <h3>Task 4</h3>
      <div className={css.card}>
        <div onClick={handleLike} className={css.like}>
          {like}
        </div>
        <div onClick={handleDislike} className={css.dislike}>
          {dislike}
        </div>
      </div>
    </div>
  );
}

export default Task4;

/* Task 4
